mapper.select:
select gt.*, gy.typename
from goodstable gt, goodstype gy
where gt.goodstype_id=gy.id and gt.id=#{id}

select gt.*, gy.typename from goodstable gt, goodstype gy where gt.goodstype_id=gy.id and gt.isRecommend=1
and gy.id=#{tid}
order by gt.id desc limit 6

select gt.*, gy.typename from goodstable gt, goodstype gy where gt.goodstype_id=gy.id
and gy.id=#{tid}
order by gt.id desc limit 6
